import { space } from "@artsy/palette"
import { fadeIn, fadeOut, growAndFadeIn } from "Assets/Animations"
import Colors from "Assets/Colors"
import { garamond, unica } from "Assets/Fonts"
import { ClosedEye } from "Assets/Icons/ClosedEye"
import { OpenEye } from "Assets/Icons/OpenEye"
import React from "react"
import styled from "styled-components"
import { block } from "./Helpers"
import { borderedInput } from "./Mixins"

export interface InputProps extends React.HTMLProps<HTMLInputElement> {
  block?: boolean
  description?: string
  error?: string
  label?: string
  leftView?: JSX.Element
  rightView?: JSX.Element
  setTouched?: (fields: { [field: string]: boolean }) => void
  showPasswordMessage?: boolean
  title?: string
  quick?: boolean
  touchedOnChange?: boolean
}

export interface InputState {
  focused: boolean
  value: string
  showPassword: boolean
}

/**
 * Configurable input field. It comes in two modes, standard & quick.
 * In standard mode, the `title` and `description` props are rendered above
 * the input. In quick mode, `title` and `description` are ignored, only
 * `label` is rendered inside the input.
 *
 * @example
 *
 * ```javascript
 *  // Quick mode
 *  <Input
 *    quick
 *    label="Name"
 *    type="text"
 *    placeholder="Enter you name"
 *  />
 *  ```
 *
 * ```
 *  // Standard mode
 *  <Input
 *    title="Name"
 *    description="Your full name."
 *    type="text"
 *  />
 */
export class Input extends React.Component<InputProps, InputState> {
  state = {
    focused: false,
    value: (this.props.value as string) || "",
    showPassword: false,
    touchedOnChange: true,
  }

  componentWillReceiveProps(newProps) {
    if (this.props.name !== newProps.name) {
      this.setState({
        value: "",
      })
    }
  }

  onFocus = e => {
    this.setState({
      focused: true,
    })

    if (this.props.onFocus) {
      this.props.onFocus(e)
    }
  }

  onBlur = e => {
    if (this.props.setTouched) {
      this.props.setTouched({ [this.props.name]: true })
    }
    this.setState({
      focused: false,
    })

    if (this.props.onBlur) {
      this.props.onBlur(e)
    }
  }

  onChange = e => {
    if (this.props.touchedOnChange && this.props.setTouched) {
      this.props.setTouched({ [this.props.name]: true })
    }
    this.setState({
      value: e.currentTarget.value,
    })

    if (this.props.onChange) {
      this.props.onChange(e)
    }
  }

  getRightViewForPassword() {
    const icon = this.state.showPassword ? (
      <ClosedEye onClick={this.toggleShowPassword} />
    ) : (
      <OpenEye onClick={this.toggleShowPassword} />
    )

    return <Eye onClick={this.toggleShowPassword}>{icon}</Eye>
  }

  toggleShowPassword = e => {
    this.setState({
      showPassword: !this.state.showPassword,
    })
  }

  get convertedType() {
    const { type } = this.props
    if (this.state.showPassword && type === "password") {
      return "text"
    }
    return type
  }

  render() {
    const { error, quick } = this.props

    if (quick) {
      // prettier-ignore
      const {
        className,
        label,
        leftView,
        ref: _ref,
        rightView,
        showPasswordMessage,
        type,
        onChange,
        setTouched,
        ...newProps
      } = this.props
      const showLabel = (!!this.state.focused || !!this.state.value) && !!label
      const isPassword = type === "password"

      return (
        <Container>
          <InputContainer
            hasLabel={!!label}
            hasError={!!error}
            className={this.state.focused ? "focused" : ""}
          >
            <Label out={!showLabel}>{label}</Label>
            {!!leftView && leftView}
            <InputComponent
              {...newProps}
              onFocus={this.onFocus}
              onBlur={this.onBlur}
              onChange={this.onChange}
              value={this.state.value}
              type={this.convertedType}
              showLabel={showLabel}
            />
            {isPassword
              ? this.getRightViewForPassword()
              : !!rightView && rightView}
          </InputContainer>
          {!error && showPasswordMessage ? (
            <PasswordMessage>
              Password must be at least 8 characters.
            </PasswordMessage>
          ) : (
            ""
          )}
          <Error show={!!error}>{error}</Error>
        </Container>
      )
    }

    const { title, description, ref, ...rest } = this.props
    return (
      <Container>
        {title && <Title>{title}</Title>}
        {description && <Description>{description}</Description>}
        <StyledInput hasError={!!error} {...rest} />
        <Error show={!!error}>{error}</Error>
      </Container>
    )
  }
}

const Container = styled.div`
  padding-bottom: 5px;
`

const StyledInput = styled.input`
  ${borderedInput};
  ${block(24)};
`

const InputComponent = styled.input.attrs<{ showLabel: boolean }>({})`
  ${garamond("s17")};
  border: 0;
  font-size: 17px;
  outline: none;
  flex: 1;
  transition: all 0.25s;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  padding: 0 ${space(1)}px;
  line-height: initial;
  ${props => props.showLabel && "padding: 10px 10px 0 10px"};

  &::placeholder {
    color: ${Colors.grayMedium};
  }
`

const InputContainer = styled.div.attrs<{
  hasLabel?: boolean
  hasError: boolean
}>({})`
  ${borderedInput};
  margin-right: 0;
  margin-top: 5px;
  margin-bottom: 10px;
  display: flex;
  position: relative;
  height: ${p => (p.hasLabel ? "40px" : "20px")};
  flex-direction: row;
  align-items: center;
  box-sizing: content-box;
`

const Label = styled.label.attrs<{ out: boolean }>({})`
  ${unica("s12", "medium")};
  position: absolute;
  left: 10px;
  top: 7px;
  visibility: ${p => (p.out ? "hidden" : "visible")};
  animation: ${p => (p.out ? fadeOut : fadeIn)} 0.2s linear;
  transition: visibility 0.2s linear;
  z-index: 1;
`

const Title = styled.div`
  ${garamond("s17")};
`

const Description = styled.div`
  ${garamond("s15")};
  color: ${Colors.graySemibold};
  margin: 3px 0 0;
`

const Error = styled.div.attrs<{ show: boolean }>({})`
  ${unica("s12")};
  margin-top: ${p => (p.show ? "10px" : "0")};
  color: ${Colors.redMedium};
  visibility: ${p => (p.show ? "visible" : "hidden")};
  transition: visibility 0.2s linear;
  animation: ${p => p.show && growAndFadeIn("16px")} 0.25s linear;
  height: ${p => (p.show ? "16px" : "0")};
`

const PasswordMessage = styled.div`
  ${unica("s12")};
  margin-top: 10px;
  color: ${Colors.graySemibold};
  height: 16px;
`

const Eye = styled.span`
  position: absolute;
  right: 10px;
  z-index: 1;
`

export default Input
