import styled from "styled-components"
import React from "react"
import { map } from "lodash"
import { garamond, unica } from "Assets/Fonts"
import fillwidthDimensions from "../../../Utils/fillwidth"
import { ToolTipDescription } from "./Components/Description"
import { FollowButton } from "./Components/FollowButton"
import { NewFeature } from "./Components/NewFeature"
import { ArtistMarketData } from "./Components/ArtistMarketData"

export interface ArtistProps {
  auctionResults?: {
    edges: Array<{
      node: {
        price_realized: {
          display: string
        }
      }
    }>
  } | null
  blurb?: string
  collections?: string[]
  formatted_nationality_and_birthday?: string
  genes?: Array<{
    name: string
  }>
  highlights?: {
    partners?: {
      edges?: Array<{
        node?: {
          categories: Array<{
            id: string
          }>
        }
      }>
    } | null
  } | null
  href?: string
  carousel?: {
    images?: Array<{
      resized: {
        url: string
        width: number
        height: number
      }
    }>
  }
  name?: string
  showMarketData?: boolean
}

export const ArtistToolTip: React.SFC<ArtistProps> = props => {
  const {
    blurb,
    carousel,
    formatted_nationality_and_birthday,
    href,
    name,
    showMarketData,
  } = props
  const displayImages = map(carousel.images.slice(0, 2), "resized")
  const images = fillwidthDimensions(displayImages, 320, 15, 150)

  return (
    <Wrapper>
      <ArtistContainer href={href}>
        {images && (
          <Images>
            {images.map((img, i) => (
              <img
                key={i}
                src={img.__id}
                width={img.width}
                height={img.height}
              />
            ))}
          </Images>
        )}

        <Header>
          <TitleDate>
            <Title>{name}</Title>
            {formatted_nationality_and_birthday && (
              <Date>{formatted_nationality_and_birthday}</Date>
            )}
          </TitleDate>
          <FollowButton /> {/* TODO: Replace with relay follow */}
        </Header>

        {showMarketData ? (
          <ArtistMarketData {...props} />
        ) : (
          blurb && <ToolTipDescription text={blurb} />
        )}
      </ArtistContainer>

      <NewFeature />
    </Wrapper>
  )
}

const Wrapper = styled.div`
  width: 320px;
`

export const ArtistContainer = styled.a`
  position: relative;
  text-decoration: none;
  color: black;
`

const Header = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`

const TitleDate = styled.div`
  display: flex;
  flex-direction: column;
`

const Title = styled.div`
  ${garamond("s18")};
  font-weight: 600;
`

const Date = styled.div`
  ${unica("s14", "medium")};
`

const Images = styled.div`
  width: 100%;
  margin-bottom: 20px;
  display: flex;
  justify-content: space-between;
`

