import styled from "styled-components"
import React, { Component } from "react"
import { garamond } from "Assets/Fonts"
import { ClassicText } from "./Layouts/ClassicText"
import { FeatureText } from "./Layouts/FeatureText"
import { ArticleLayout } from "../../Typings"
import Colors from "Assets/Colors"

interface Props {
  color?: string
  isContentStart?: boolean
  layout: ArticleLayout
  postscript?: Boolean
  showTooltips?: boolean
}

export class StyledText extends Component<Props> {
  getLayout = () => {
    const { children, layout } = this.props

    switch (layout) {
      case "classic": {
        return <ClassicText {...this.props}>{children}</ClassicText>
      }
      case "feature": {
        return <FeatureText {...this.props}>{children}</FeatureText>
      }
      default: {
        return children
      }
    }
  }

  render() {
    return <StyledTextContainer>{this.getLayout()}</StyledTextContainer>
  }
}

const StyledTextContainer = styled.div`
  position: relative;
  width: 100%;

  a {
    color: black;
    background-image: linear-gradient(
      to bottom,
      transparent 0,
      black 1px,
      transparent 0
    );
    text-decoration: none;
    background-size: 1.25px 4px;
    background-repeat: repeat-x;
    background-position: bottom;
    position: relative;
  }

  h2 a,
  h3 a {
    background-size: 1.25px 1px;
  }

  a:hover {
    color: ${Colors.grayDark};
  }

  /* Use margin/padding from section container for 1st P */
  p:first-child,
  .paragraph:first-child,
  div[data-block="true"]:first-child .public-DraftStyleDefault-block {
    padding-top: 0;
    margin: 0;
  }

  /* Use margin/padding from section container for last P */
  p:last-child,
  .paragraph:last-child,
  div[data-block="true"]:last-child .public-DraftStyleDefault-block {
    padding-bottom: 0;
    margin: 0;
  }

  ol {
    list-style: decimal;
    padding-left: 1em;
    margin: 0;
  }

  ul {
    list-style: disc;
    padding-left: 1em;
    margin: 0;
  }

  li {
    padding-top: 0.5em;
    padding-bottom: 0.5em;
  }

  h2 {
    font-weight: normal;
    margin: 0;
  }

  blockquote {
    padding-top: 46px;
    padding-bottom: 46px;
    margin: 0;
    word-break: break-word;
  }

  .artist-follow {
    vertical-align: middle;
    margin-left: 10px;
    cursor: pointer;
    background: none transparent;

    &::before {
      font-family: "artsy-icons";
      content: "\ue629";
      vertical-align: -8.5px;
      line-height: 32px;
      font-size: 32px;
    }

    &::after {
      content: "Follow";
      ${garamond("s17")};
      text-transform: none;
    }
  }
`
