import React, { Component, HTMLProps } from "react"
import styled from "styled-components"
import colors from "../Assets/Colors"
import { Checkmark } from "Assets/Checkmark"
import { garamond } from "Assets/Fonts"

interface CheckboxState {
  checked: boolean
}

export interface CheckboxProps extends HTMLProps<HTMLInputElement> {
  error?: boolean
}

export class Checkbox extends Component<CheckboxProps, CheckboxState> {
  constructor(props) {
    super(props)

    this.state = {
      checked: props.checked || false,
    }
  }

  onChange = event => {
    if (this.props.onChange) {
      this.props.onChange(event)
    }

    this.setState({
      checked: event.currentTarget.checked,
    })
  }

  render() {
    const {
      children,
      className,
      disabled,
      error,
      ref,
      ...remainderProps
    } = this.props
    const { checked } = this.state

    return (
      <Label className={className} error={error}>
        <CheckmarkContainer>
          <CheckboxInput
            {...remainderProps}
            type="checkbox"
            onChange={this.onChange}
            checked={checked}
            disabled={disabled}
            error={error}
          />

          {(!disabled || checked) && (
            <PositionedCheckmark
              stroke={disabled && checked && colors.black30}
            />
          )}
        </CheckmarkContainer>

        {children}
      </Label>
    )
  }
}

const CheckmarkContainer = styled.div`
  width: 20px;
  height: 20px;
  top: -1px;
  margin-right: 0.5rem;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
`

const PositionedCheckmark = styled(Checkmark)`
  z-index: 1;
  margin-top: 1px;
`

const CheckboxInput = styled.input.attrs<CheckboxProps>({})`
  width: 20px;
  height: 20px;
  position: absolute;
  margin: 0;

  // The after represents the square box
  &:after {
    transition: all 0.25s;
    content: "";
    position: absolute;
    width: 20px;
    height: 20px;
    box-sizing: border-box;
    background-color: ${colors.white};
    border: 2px solid
      ${({ error }) => (error ? colors.redMedium : colors.grayRegular)};
  }

  &:hover:after {
    background-color: ${colors.grayRegular};
    border-color: ${colors.grayRegular};
  }

  &:checked:after {
    background-color: ${colors.black};
    border-color: ${colors.black};
  }

  &:disabled:after {
    background-color: ${colors.gray};
    border-color: ${colors.grayRegular};
  }
`

const Label = styled.label.attrs<CheckboxProps>({})`
  ${garamond("s16")};
  position: relative;
  line-height: 135%;
  cursor: pointer;
  display: flex;
  align-items: center;
  ${({ error }) => error && `color: ${colors.redMedium}`};
`

export default Checkbox
