import React from "react"
import styled, { StyledFunction } from "styled-components"
import { pMedia } from "../../../../Helpers"
import { garamond, unica } from "Assets/Fonts"
import { ArticleLayout } from "../../../Typings"
import { PrimaryLink } from "../../../ToolTip/LinkWithTooltip"

interface StyledTextProps {
  color?: string
  isContentStart?: boolean
  layout: ArticleLayout
  postscript?: Boolean
  showTooltips?: boolean
}

const div: StyledFunction<StyledTextProps & React.HTMLProps<HTMLDivElement>> =
  styled.div

export const FeatureText = div`
  padding-bottom: ${props => (props.postscript ? "2em" : "")};

  div[class*='ToolTip'] a {
    background-image: none;
    opacity: 1;
    color: inherit;
  }
  p, ul, ol, .paragraph,
  div[data-block=true] .public-DraftStyleDefault-block {
    ${garamond("s23")}
    padding-top: 1em;
    padding-bottom: 1em;
    font-style: ${props => (props.postscript ? "italic" : "inherit")};
  }
  li {
    ${garamond("s23")}
  }
  h1 {
    ${unica("s40")}
    font-weight: normal;
    padding-top: 107px;
    padding-bottom: 46px;
    margin: 0;
    position: relative;
    text-align: center;
    &:before {
      content: "";
      width: 15px;
      height: 15px;
      background: black;
      border-radius: 50%;
      position: absolute;
      top: 69px;
      right: calc(50% - 7.5px);
    }
  }
  h2 {
    ${unica("s32")}
    ${PrimaryLink} {
      background-position: bottom !important;
    }
  }
  h3 {
    ${unica("s19")}
    font-weight: normal;
    padding-top: 23px;
    margin: 0;
    strong {
      font-weight: normal;
      ${unica("s19", "medium")}
    }
  }
  blockquote {
    ${unica("s40")}
    text-align: left;
    font-weight: normal;
  }

  p:first-child:first-letter,
  .paragraph:first-child:first-letter,
  div[data-block=true]:first-child .public-DraftStyleDefault-block:first-letter {
    ${props => props.isContentStart && unica("s67", "medium")}
    ${props =>
      props.isContentStart &&
      `
      float: left;
      line-height: .5em;
      margin-right: 10px;
      margin-top: .298em;
      text-transform: uppercase;
  `}
  }

  .content-end {
    display: inline-block;
    content: "";
    width: 12px;
    height: 12px;
    background: black;
    border-radius: 50%;
    margin-left: 12px;
  }

  ${props => pMedia.xs`
    p, ul, ol, li,
    div[data-block=true] .public-DraftStyleDefault-block {
      ${garamond("s19")}
    }
    h1, blockquote {
      ${unica("s34")}
    }
    h2 {
      ${unica("s32")}
    }
    h3 {
      ${unica("s16")}
    }
    .content-start {
      font-size: 55px;
    }
  `}
`

FeatureText.defaultProps = {
  color: "black",
}
