import React from "react"
import styled from "styled-components"
import request from "superagent"
import Colors from "../../../Assets/Colors"
import Events from "../../../Utils/Events"
import { track } from "../../../Utils/track"
import InvertedButton from "../../Buttons/Inverted"
import { borderedInput } from "../../Mixins"
import { EMAIL_REGEX } from "../Constants"
import { avantgarde, unica } from "Assets/Fonts"

interface EmailPanelProps {
  signupUrl: string
  tracking?: any
}

interface EmailPanelState {
  value: string
  error: boolean
  submitted: boolean
  disabled: boolean
  message: string
}

interface InputProps extends React.HTMLProps<HTMLInputElement> {
  isError: boolean
  isReadOnly: boolean
}

@track(
  { page: "Article" },
  {
    dispatch: data => Events.postEvent(data),
  }
)
export class EmailPanel extends React.Component<
  EmailPanelProps,
  EmailPanelState
> {
  state = {
    value: "",
    error: false,
    submitted: false,
    disabled: false,
    message: "",
  }

  onClick = () => {
    this.setState({ disabled: true })
    if (this.state.value.match(EMAIL_REGEX)) {
      request
        .post(this.props.signupUrl)
        .send({ email: this.state.value })
        .set("accept", "json")
        .end((err, res) => {
          if (err) {
            this.flashMessage("Error. Please try again", true, false)
          } else {
            this.flashMessage("Thank you!", false, true)
            this.props.tracking.trackEvent({
              action: "Sign up for editorial email",
              context_type: "article_fixed",
            })
          }
        })
    } else {
      this.flashMessage("Invalid Email... Please try again", true, false)
    }
  }

  flashMessage = (message, error, submitted) => {
    this.setState({ message, error })
    setTimeout(() => {
      this.setState({ message: "", disabled: false, error: false, submitted })
    }, 2000)
  }

  onInputChange = e => {
    this.setState({ value: e.target.value, error: null })
  }

  render() {
    if (this.state.submitted) {
      return <div />
    } else {
      return (
        <EmailPanelContainer>
          <Title>Stay up to date with Artsy Editorial</Title>
          <Form>
            <Input
              type="email"
              placeholder="Enter Your Email..."
              onChange={this.onInputChange}
              value={this.state.message || this.state.value}
              readOnly={this.state.message.length > 0}
              isError={this.state.error}
              isReadOnly={this.state.message.length > 0}
            />
            <StyledButton disabled={this.state.disabled} onClick={this.onClick}>
              Subscribe
            </StyledButton>
          </Form>
        </EmailPanelContainer>
      )
    }
  }
}

const Input = styled.input`
  width: 100%;
  border-width: 1px;
  color: ${(props: InputProps) => (props.isError ? Colors.redMedium : "black")};
  ${(props: InputProps) => (props.isReadOnly ? unica("s16") : "")};
  ${borderedInput};
`
const EmailPanelContainer = styled.div`
  display: flex;
  flex-direction: column;
  max-width: 350px;
  width: 100%;
`
const Title = styled.div`
  ${unica("s16", "medium")};
  margin-bottom: 10px;
`
const StyledButton = InvertedButton.extend`
  border-radius: 2px;
  height: 30px;
  width: 80px;
  margin-left: -100px;
  ${avantgarde("s11")};
`
const Form = styled.div`
  display: flex;
`
