import React from "react"
import { Col } from "react-styled-flexboxgrid"
import styled from "styled-components"
import { media } from "../../Helpers"
import { ArticleCard, ArticleCardContainer } from "../Series/ArticleCard"
import { ArticleData } from "../Typings"

interface Props {
  article?: ArticleData
  color?: string
  relatedArticles?: any
}

export const RelatedArticleCards: React.SFC<Props> = props => {
  const { article, color, relatedArticles } = props
  const { seriesArticle } = article

  return (
    <RelatedArticlesContainer color={color}>
      {relatedArticles.map((relatedArticle, i) => {
        return (
          <Col xs={12} key={i}>
            <ArticleCard
              article={relatedArticle}
              color={color}
              series={seriesArticle}
            />
          </Col>
        )
      })}
    </RelatedArticlesContainer>
  )
}

const RelatedArticlesContainer = styled.div`
  color: ${props => (props.color ? props.color : "black")};
  ${ArticleCardContainer} {
    margin-bottom: 60px;
  }
  ${media.sm`
    ${ArticleCardContainer} {
      margin-bottom: 30px;
    }
  `};
`
