import React from "react"
import styled, { StyledFunction } from "styled-components"
import { pMedia } from "../../../../Helpers"
import { avantgarde, garamond } from "Assets/Fonts"

const div: StyledFunction<React.HTMLProps<HTMLDivElement>> = styled.div

export const ClassicText = div`
  p, ul, ol, .paragraph,
  div[data-block=true] .public-DraftStyleDefault-block {
    ${garamond("s19")}
    padding-top: 0.75em;
    padding-bottom: 0.75em;
  }

  li {
    ${garamond("s19")}
  }

  h2 {
    ${garamond("s28")}

  }
  h3 {
    ${avantgarde("s13")}
    font-weight: normal;
    padding-top: 23px;
    margin: 0;
    strong, em {
      font-weight: normal;
    }
  }
  blockquote {
    ${garamond("s40")}
    text-align: center;
    font-weight: normal;
    padding-top: 46px;
    padding-bottom: 46px;
    margin: 0;
    word-break: break-word;
  }
  ${props => pMedia.xs`
    p, ul, ol,
    div[data-block=true] .public-DraftStyleDefault-block {
      ${garamond("s19")}
    }
    li {
      ${garamond("s19")}
    }
    h2 {
      ${garamond("s28")}
    }
    h3 {
      ${avantgarde("s11")}
      line-height: 1.5em;
    }
    blockquote {
      ${garamond("s34")};
      margin: 0;
    }
  `}
`
