import React from "react"
import styled from "styled-components"
import { Header } from "../Header/Header"
import { Nav } from "../Nav/Nav"
import { RelatedArticlesCanvas } from "../RelatedArticles/RelatedArticlesCanvas"
import { Sections } from "../Sections/Sections"
import { ArticleData } from "../Typings"

export interface ArticleProps {
  article: ArticleData
  seriesArticle?: ArticleData
  headerHeight?: string
  isMobile?: boolean
  isSuper?: boolean
  marginTop?: string
  relatedArticlesForCanvas?: any
}

export class FeatureArticle extends React.Component<ArticleProps> {
  render() {
    const {
      article,
      headerHeight,
      isMobile,
      isSuper,
      relatedArticlesForCanvas,
      seriesArticle
    } = this.props
    const sponsor = seriesArticle ? seriesArticle.sponsor : article.sponsor

    return (
      <div>
        {seriesArticle &&
          <Nav sponsor={sponsor} />
        }
        <Header article={article} height={headerHeight} isMobile={isMobile} />

        <FeatureLayout className="article-content">
          <Sections article={article} isMobile={isMobile} />
        </FeatureLayout>

        {relatedArticlesForCanvas &&
          !isSuper &&
          !seriesArticle && (
            <RelatedArticlesCanvas
              articles={relatedArticlesForCanvas}
              vertical={article.vertical}
            />
          )}
      </div>
    )
  }
}

export const FeatureLayout = styled.div`
  display: flex;
  width: 100%;
`
